import * as PIXI from "pixi.js";
import gsap from "gsap";

window.PIXI = PIXI;

/**
 * interactive portfolio list with webGL preview interaction powered by PIXI.js
 */
export default class HotspotsController {
    constructor() {
        /**
         *
         * @type {{hotspotText: string, hotspot: string, hotspotCircle: string, hotspotCanvasContainer: string, hotspotContainer: string}}
         */
        this.DOM = {
            hotspotContainer: ".js-hotspot-container",
            hotspot: ".js-hotspot",
            hotspotCircle: ".js-hotspot-circle",
            hotspotText: ".js-hotspot-text",
        };

        /**
         *
         * @returns {NodeListOf<Element>}
         */
        this.hotspotContainer = document.querySelectorAll(
            this.DOM.hotspotContainer,
        );
    }

    init() {
        console.log("HotspotsController init()");

        if (this.hotspotContainer !== null) {
            this.hotspotLines();
        } else {
            console.error(
                `${this.DOM.hotspotContainer} does not exist in the DOM!`,
            );
        }
    }

    hotspotLines() {
        const lineColor = 0xb1b1b1;
        const lineGradient = ["#b1b1b1", "#ffffff"];
        const lineEndingRadius = 2;

        const gradientTexture = this.createGradientTexture(
            lineGradient[0],
            lineGradient[1],
        );

        for (let i = 0; i < this.hotspotContainer.length; i++) {
            const hotspots = this.hotspotContainer[i].querySelectorAll(
                this.DOM.hotspot,
            );

            // CANVAS SIZE
            const canvasWidth = this.hotspotContainer[i].clientWidth;
            const canvasHeight = this.hotspotContainer[i].clientHeight;

            // CREATE PIXI APPLICATION
            const app = new PIXI.Application({
                width: canvasWidth,
                height: canvasHeight,
                antialias: true,
                transparent: true,
                resolution: window.devicePixelRatio,
                resizeTo: this.hotspotContainer[i],
            });

            // ADD CANVAS TO CANVAS WRAPPER ELEMENT
            this.hotspotContainer[i].appendChild(app.view);

            for (let i = 0; i < hotspots.length; i++) {
                const circle = hotspots[i].querySelector(
                    this.DOM.hotspotCircle,
                );
                const text = hotspots[i].querySelector(this.DOM.hotspotText);

                const line = new PIXI.Graphics();
                line.alpha = 0;

                app.stage.addChild(line);

                app.ticker.add(() => {
                    line.clear();
                    line.beginFill(lineColor, 1);
                    line.drawCircle(
                        this.getElementPosition(circle)[0],
                        this.getElementPosition(circle)[1],
                        lineEndingRadius,
                    );
                    line.endFill();
                    line.beginFill(lineColor, 1);
                    line.drawCircle(
                        this.getElementPosition(text)[0],
                        this.getElementPosition(text)[1],
                        lineEndingRadius,
                    );
                    line.endFill();
                    line.moveTo(
                        this.getElementPosition(circle)[0],
                        this.getElementPosition(circle)[1],
                    );
                    line.lineStyle(1, lineColor, 1);
                    line.lineTextureStyle({
                        width: 1,
                        texture: gradientTexture,
                        color: 0xffffff,
                    });
                    line.lineTo(
                        this.getElementPosition(text)[0],
                        this.getElementPosition(text)[1],
                    );
                });

                //hover
                hotspots[i].addEventListener("mouseenter", () => {
                    gsap.to(line, {
                        alpha: 1,
                        duration: 0.4,
                        onComplete: () => {},
                    });
                });

                hotspots[i].addEventListener("mouseleave", () => {
                    gsap.to(line, {
                        alpha: 0,
                        duration: 0.4,
                        onComplete: () => {},
                    });
                });
            }
        }
    }

    /**
     *
     * @param {HTMLElement} element - element which position we should get
     * @returns {[number, number]} - position of element relative to viewport
     */
    getElementPosition(element) {
        const posX =
            element.getBoundingClientRect().left -
            element.closest(this.DOM.hotspotContainer).getBoundingClientRect()
                .left +
            element.clientWidth / 2;

        const posY =
            element.getBoundingClientRect().top -
            element.closest(this.DOM.hotspotContainer).getBoundingClientRect()
                .top +
            element.clientHeight / 2;

        return [posX, posY];
    }

    /**
     *
     * @param {string} from - color in hex format
     * @param {string} to - color in hex format
     * @returns {PIXI.Texture}
     */
    createGradientTexture(from, to) {
        const textureWH = 600;
        const canvas = document.createElement("canvas");
        const context = canvas.getContext("2d");
        const gradient = context.createLinearGradient(
            0,
            0,
            textureWH,
            textureWH,
        );
        gradient.addColorStop(0, from);
        gradient.addColorStop(1, to);
        context.fillStyle = gradient;
        context.fillRect(0, 0, textureWH, textureWH);

        return new PIXI.Texture.from(canvas);
    }
}

import gsap from "gsap";

export default class MagneticCtaController {
    constructor() {
        this.DOM = {
            ctaContainer: ".js-magnetic-cta-container",
            cta: ".js-magnetic-cta",
            ctaContent: ".js-magnetic-cta-content",
        };

        this.ctaContainer = document.querySelectorAll(this.DOM.ctaContainer);
    }

    init() {
        console.log("MagneticCtaController init()");

        if (this.ctaContainer !== null) {
            this.magneticCtaEvents();
        } else {
            console.error(
                `${this.DOM.ctaContainer} does not exist in the DOM!`,
            );
        }
    }

    magneticCtaEvents() {
        for (let i = 0; i < this.ctaContainer.length; i++) {
            this.ctaContainer[i].addEventListener("mousemove", (ev) => {
                this.magneticMousemove(ev);
            });

            this.ctaContainer[i].addEventListener("mouseleave", (ev) => {
                this.magneticMouseleave(ev);
            });
        }
    }

    magneticMousemove(ev) {
        ev.currentTarget.classList.add("is-hovered");

        // Get X-coordinate for the left container edge
        const containerPosX = ev.currentTarget.getBoundingClientRect().left;
        const containerPosY = ev.currentTarget.getBoundingClientRect().top;

        // Get position of the mouse inside element from left edge
        // (current mouse X position - button x coordinate)
        const pageX = ev.clientX;
        const pageY = ev.clientY;

        const xPosOfMouse = pageX - containerPosX;
        const yPosOfMouse = pageY - containerPosY;

        // Get position of mouse relative to container center
        // Mouse position inside element - container width / 2
        // To get positive or negative movement
        const xPosOfMouseInsideContainer =
            xPosOfMouse - ev.currentTarget.offsetWidth / 2;
        const yPosOfMouseInsideContainer =
            yPosOfMouse - ev.currentTarget.offsetHeight / 2;

        // Button text divider to increase or decrease text path
        const animationDivider = 3;
        const animationDividerText = 1.5;

        // Animate button text positive or negative from center
        gsap.to(ev.currentTarget.querySelector(this.DOM.cta), 0.3, {
            x: xPosOfMouseInsideContainer / animationDivider,
            y: yPosOfMouseInsideContainer / animationDivider,
            ease: "power3.out",
        });

        if (
            ev.currentTarget.querySelector(this.DOM.ctaContent).length !== null
        ) {
            gsap.to(ev.currentTarget.querySelector(this.DOM.ctaContent), 0.2, {
                x: xPosOfMouseInsideContainer / animationDividerText,
                y: yPosOfMouseInsideContainer / animationDividerText,
                ease: "power3.out",
            });
        }
    }

    magneticMouseleave(ev) {
        ev.currentTarget.classList.remove("is-hovered");

        // Animate button text reset to initial position (center)
        gsap.to(ev.currentTarget.querySelector(this.DOM.cta), {
            duration: 0.3,
            x: 0,
            y: 0,
            ease: "power3.out",
        });

        if (
            ev.currentTarget.querySelector(this.DOM.ctaContent).length !== null
        ) {
            gsap.to(ev.currentTarget.querySelector(this.DOM.ctaContent), {
                duration: 0.5,
                x: 0,
                y: 0,
                ease: "power3.out",
            });
        }
    }
}

/**
 * "smart" navigation which goes off screen when scrolling down for a better overview of content and UX
 * navigation appears when scrolling up
 */
export default class NavigationController {
    constructor() {
        /**
         * Navigation DOM selectors
         * Navigation DOM state CSS classes
         * @type {{navigation: string, states: {navigationSlideUp: string, navigationScrolled: string, navigationFixed: string}}}
         */

        this.DOM = {
            navigation: ".js-navigation-wrapper",
            states: {
                navigationScrolled: "has-scrolled",
                navigationFixed: "is-fixed",
                navigationSlideUp: "slide-up",
            },
        };

        /**
         * flag, state variable for scrolling event
         * @type {boolean}
         */
        this.scrolling = false;
        /**
         * amount of pixels to scroll from top for adding "has-scrolled" state class
         * @type {number}
         */
        this.scrollNavigationOffset = 200;
        /**
         * variable for storing amount of scroll from top position value
         * @type {number}
         */
        this.previousTop = 0;
        /**
         * variable for storing current scroll position value
         * @type {number}
         */
        this.currentTop = 0;
        this.scrollDelta = 0;
        this.scrollOffset = 0;

        /**
         * fetch navigation element DOM element
         * @type {Element}
         */
        this.navigation = document.querySelector(this.DOM.navigation);
    }

    //region methods
    /**
     *
     */
    init() {
        console.log("Navigation init()");

        if (this.navigation !== null) {
            this.navigationController();
        } else {
            console.error(`${this.DOM.navigation} does not exist in the DOM!`);
        }
    }

    /**
     *
     */
    navigationController() {
        document.addEventListener("scroll", () => {
            if (!this.scrolling) {
                this.scrolling = true;

                if (!window.requestAnimationFrame) {
                    setTimeout(this.checkScroll(), 250);
                } else {
                    requestAnimationFrame(() => this.checkScroll());
                }
            }
        });
    }

    /**
     *
     */
    checkScroll() {
        /**
         *
         * @type {number}
         */
        let currentTop = window.pageYOffset | document.body.scrollTop;

        this.changeNavigationState(currentTop);

        this.previousTop = currentTop;
        this.scrolling = false;
    }

    /**
     *
     * @param currentTop
     */
    changeNavigationState(currentTop) {
        if (currentTop > this.scrollNavigationOffset) {
            this.navigation.classList.add(this.DOM.states.navigationScrolled);
        } else {
            this.navigation.classList.remove(
                this.DOM.states.navigationScrolled,
            );
        }

        if (this.previousTop >= currentTop) {
            this.scrollingUp(currentTop);
        } else {
            this.scrollingDown(currentTop);
        }
    }

    /**
     *
     * @param currentTop
     */
    scrollingUp(currentTop) {
        if (currentTop < this.scrollNavigationOffset) {
            this.navigation.classList.remove(this.DOM.states.navigationSlideUp);
        } else if (this.previousTop - currentTop > this.scrollDelta) {
            this.navigation.classList.remove(this.DOM.states.navigationSlideUp);
        }
    }

    /**
     *
     * @param currentTop
     */
    scrollingDown(currentTop) {
        if (currentTop > this.scrollNavigationOffset + this.scrollOffset) {
            this.navigation.classList.add(this.DOM.states.navigationSlideUp);
        } else if (currentTop > this.scrollNavigationOffset) {
            this.navigation.classList.remove(this.DOM.states.navigationSlideUp);
        }
    }

    //endregion
}

import * as PIXI from "pixi.js";
import gsap from "gsap";

window.PIXI = PIXI;

/**
 * interactive portfolio list with webGL preview interaction powered by PIXI.js
 */
export default class PortfolioListController {
    constructor() {
        /**
         * Navigation DOM selectors
         * Navigation DOM state CSS classes
         * @type {{navigation: string, states: {navigationSlideUp: string, navigationScrolled: string, navigationFixed: string}}}
         */

        this.DOM = {
            portfolio: ".js-portfolio",
            portfolioItem: ".js-portfolio-item",
            portfolioPreviewList: ".js-portfolio-preview-list",
            portfolioPreviewItem: ".js-portfolio-preview-item",
            portfolioCanvas: ".js-portfolio-canvas",
        };

        /**
         *
         * @type {Element}
         */
        this.portfolio = document.querySelector(this.DOM.portfolio);

        /**
         *
         * @type {NodeListOf<Element>}
         */
        this.portfolioItems = document.querySelectorAll(this.DOM.portfolioItem);

        /**
         *
         * @type {Element}
         */
        this.portfolioPreviewList = document.querySelector(
            this.DOM.portfolioPreviewList,
        );

        /**
         *
         * @type {NodeListOf<Element>}
         */
        this.portfolioPreviewItems = document.querySelectorAll(
            this.DOM.portfolioPreviewItem,
        );

        /**
         *
         * @type {Element}
         */
        this.portfolioCanvas = document.querySelector(this.DOM.portfolioCanvas);

        //PIXI stuff
        this.app = null;

        //gsap stuff
        this.displacementTimeline = null;
    }

    //region methods

    init() {
        console.log("PortfolioListController init()");

        if (this.portfolio !== null) {
            this.portfolioEvents();
            this.portfolioController();
        } else {
            console.error(`${this.DOM.portfolio} does not exist in the DOM!`);
        }
    }

    portfolioEvents() {
        for (let i = 0; i < this.portfolioItems.length; i++) {
            this.portfolioItems[i].addEventListener("mousemove", (ev) => {
                this.portfolioMousemove(ev);
            });

            this.portfolioItems[i].addEventListener("mouseenter", () => {
                this.portfolioItemMouseenter(i + 1); // why +1? Hint: PIXI related
            });

            this.portfolioItems[i].addEventListener("mouseleave", () => {
                this.portfolioItemMouseleave();
            });
        }
    }

    portfolioController() {
        // predefine canvas element dimensions in px 16:9
        const canvasWidth = 1000;
        const canvasHeight = 563;

        // assign/create new PIXI application which automatically creates the renderer, ticker, and root container/stage
        this.app = new PIXI.Application({
            width: canvasWidth,
            height: canvasHeight,
            resolution: window.devicePixelRatio,
            transparent: true,
        });

        // add PIXI canvas element to our DOM element
        this.portfolioCanvas.appendChild(this.app.view);

        const displacementMapImage = this.portfolioCanvas.getAttribute(
            "data-displacement-map",
        );

        // create displacement texture
        const displacementMap = new PIXI.Sprite.from(displacementMapImage);

        // create PIXI displacement filter and pass the texture
        const displacementFilter = new PIXI.filters.DisplacementFilter(
            displacementMap,
        );

        // set displacement texture properties
        displacementMap.name = displacementMapImage;
        displacementMap.anchor.set(0.5);
        displacementMap.scale.set(1);
        displacementMap.width = canvasWidth;
        displacementMap.height = canvasHeight;
        displacementMap.position.set(canvasWidth / 2, canvasHeight / 2);

        // set stage properties for filter
        this.app.stage.filterArea = this.app.screen;
        this.app.stage.filters = [displacementFilter];

        // add filter to root container/stage
        this.app.stage.addChild(displacementMap);

        // load images from DOM to PIXI container
        for (let i = 0; i < this.portfolioItems.length; i++) {
            //create texture
            const texture = new PIXI.Texture.from(
                this.portfolioPreviewItems[i].getAttribute(
                    "data-portfolio-preview",
                ),
            );

            // create sprite and pass the texture
            const image = new PIXI.Sprite(texture);

            //set sprites properties
            image.name = this.portfolioPreviewItems[i].getAttribute(
                "data-portfolio-preview",
            );
            image.alpha = 1;
            image.width = canvasWidth;
            image.height = canvasHeight;

            // add sprites to root container/stage
            this.app.stage.addChild(image);
        }

        // DISPLACE TIMELINE
        this.displacementTimeline = gsap.timeline({
            paused: true,
        });

        const canvasElement = this.portfolioCanvas.querySelector("canvas");

        this.displacementTimeline
            .add("start")
            .fromTo(
                canvasElement,
                {
                    autoAlpha: 0,
                },
                {
                    duration: 0.4,
                    autoAlpha: 1,
                    ease: "power3.out",
                },
                "start",
            )
            .fromTo(
                canvasElement,
                {
                    scale: 1.5,
                },
                {
                    duration: 0.8,
                    scale: 1,
                    ease: "power3.out",
                },
                "start",
            )
            .fromTo(
                displacementFilter.scale,
                {
                    x: 150,
                    y: 150,
                },
                {
                    duration: 1.6,
                    x: 0,
                    y: 0,
                    ease: "power3.out",
                    onComplete: () => {},
                },
                "start",
            );
    }

    portfolioMousemove(ev) {
        const decimalX = ev.clientX / window.innerWidth - 0.5;
        const decimalY = ev.clientY / window.innerHeight - 0.5;

        gsap.to(this.portfolioCanvas, {
            duration: 0.4,
            x: 300 * decimalX,
            y: 150 * decimalY,
            ease: "power3.out",
        });
    }

    portfolioItemMouseenter(index) {
        gsap.to(this.app.stage.children[index], {
            duration: 0.4,
            alpha: 1,
            ease: "power3.out",
            onStart: () => {
                this.displacementTimeline.restart().pause();
                this.displacementTimeline.play();
            },
        });
    }

    portfolioItemMouseleave() {
        gsap.to(this.app.stage.children, {
            duration: 0.4,
            alpha: 0,
            ease: "power3.out",
        });
    }

    //endregion
}

import * as PIXI from "pixi.js";

import gsap from "gsap";

export default class ThreeDPhotosController {
    constructor() {
        this.DOM = {
            threeDPhotoContainer: ".js-3d-photo-container",
            threeDPhoto: ".js-3d-photo",
            threeDPhotoCanvas: ".js-3d-photo-canvas",
        };

        this.threeDPhotos = document.querySelectorAll(this.DOM.threeDPhoto);
    }

    init() {
        console.log("ThreeDPhotos init()");

        if (this.threeDPhotos !== null) {
            this.threeDPhotosController();
        } else {
            console.error(`${this.DOM.threeDPhoto} does not exist in the DOM!`);
        }
    }

    threeDPhotosController() {
        for (let i = 0; i < this.threeDPhotos.length; i++) {
            //MOUSEMOVE CONTAINER
            const container = this.threeDPhotos[i].closest(
                this.DOM.threeDPhotoContainer,
            );

            //THRESHOLD
            const verticalThreshold = this.threeDPhotos[i].getAttribute(
                "data-vertical-threshold",
            );
            const horizontalThreshold = this.threeDPhotos[i].getAttribute(
                "data-horizontal-threshold",
            );

            // CANVAS SIZE
            const canvasWidth = this.threeDPhotos[i].clientWidth;
            const canvasHeight = this.threeDPhotos[i].clientHeight;

            // CREATE PIXI APPLICATION
            const app = new PIXI.Application({
                width: canvasWidth,
                height: canvasHeight,
                transparent: true,
                resolution: window.devicePixelRatio,
                resizeTo: this.threeDPhotos[i],
            });

            // ADD CANVAS TO CANVAS WRAPPER ELEMENT
            this.threeDPhotos[i].appendChild(app.view);

            //IMAGE
            const image = PIXI.Sprite.from(
                this.threeDPhotos[i].getAttribute("data-image"),
            );

            image.width = canvasWidth;
            image.height = canvasHeight;

            image.anchor.set(0.5);

            image.position.x = canvasWidth / 2;
            image.position.y = canvasHeight / 2;

            app.stage.addChild(image);

            // //DEPTH MAP
            const depthMap = PIXI.Sprite.from(
                this.threeDPhotos[i].getAttribute("data-depth-map"),
            );
            const depthMapFilter = new PIXI.filters.DisplacementFilter(
                depthMap,
            );

            app.stage.addChild(depthMap);
            app.stage.filters = [depthMapFilter];

            depthMap.width = canvasWidth;
            depthMap.height = canvasHeight;

            depthMap.anchor.set(0.5);

            depthMap.position.y = canvasHeight / 2;
            depthMap.position.x = canvasWidth / 2;

            depthMapFilter.scale.x = 2;
            depthMapFilter.scale.y = 2;

            //EVENTS
            this.initThreeDEvents(
                container,
                depthMapFilter,
                horizontalThreshold,
                verticalThreshold,
            );
        }
    }

    /**
     *
     * @param {HTMLElement} container
     * @param {PIXI.filters.DisplacementFilter} displacementFilter
     * @param {number} horizontalThreshold
     * @param {number} verticalThreshold
     */
    initThreeDEvents(
        container,
        displacementFilter,
        horizontalThreshold,
        verticalThreshold,
    ) {
        container.addEventListener("mousemove", (ev) => {
            let yAmount = ev.clientY / window.innerHeight - 0.5;
            let xAmount = ev.clientX / window.innerWidth - 0.5;

            gsap.to(displacementFilter.scale, {
                duration: 2,
                y: yAmount * verticalThreshold,
                x: xAmount * horizontalThreshold,
                ease: "power3.out",
            });

            // displacementFilter.scale.x = xAmount * horizontalThreshold;
            // displacementFilter.scale.y = yAmount * verticalThreshold;
        });
    }
}
